import { createOneSpotThunk } from "../../../store/spots";
import { getOneSpot } from "../../../store/spots";
import './CreateSpot.css';
import { NavLink } from 'react-router-dom';
import { useParams, useHistory } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';



export default function CreateSpot() {
    const dispatch = useDispatch();
    const history = useHistory();
    const [address, setAddress] = useState("");
    const [country, setCountry] = useState("");
    const [error, setError] = useState({});
    const [city, setCity] = useState("");
    const [description, setDescription] = useState("");
    const [name, setName] = useState("");
    const [price, setPrice] = useState("");
    const [previewImage, setPreviewImage] = useState("");
    const [image2, setImage2] = useState("");
    const [image3, setImage3] = useState("");
    const [state, setState] = useState("")
    const [image4, setImage4] = useState("");
    const [image5, setImage5] = useState("");
    const [lat, setLat] = useState("");
    const [lng, setLng] = useState("");

    const onSubmit = async (e) => {
        e.preventDefault();
        const error = {}
        if (!price || !parseInt(price)) {
            error.price = "Price is required and must be a number."
        }
        if (!address) {
            error.address = "Address is required."
        }
        if (description.length < 30) {
            error.description = "Description is required and needs to be at least 30 characters long."
        }
        if (!name) {
            error.name = "Name is required."
        }
        if (!city) {
            error.city = "City is required."
        }

        // if (price && !(Number.isInteger(price))) {
        //    error.price = "Price is required needs to be a number."
        // }
        if (!state) {
            error.state = "State is required."
        }
        if (!country) {
            error.country = "Country is required."
        }
        if (!previewImage) {
            error.previewImage = "Preview image is required."
        }
        if (previewImage) {
            if (
                !(previewImage.endsWith(".jpg") || previewImage.endsWith(".png") || previewImage.endsWith(".jpeg"))
            )
                error.previewImage = "The image url needs to end in .jpg, .png, or jpeg"
        }

        if (image2) {
            if (
                !(image2.endsWith(".jpg") || image2.endsWith(".png") || image2.endsWith(".jpeg"))
            )
                error.image2 = "The image url needs to end in .jpg, .png, or jpeg"
        }
        if (image3) {
            if (
                !(image3.endsWith(".jpg") || image3.endsWith(".png") || image3.endsWith(".jpeg"))
            )
                error.image3 = "The image url needs to end in .jpg, .png, or jpeg"
        }
        if (image4) {
            if (
                !(image4.endsWith(".jpg") || image4.endsWith(".png") || image4.endsWith(".jpeg"))
            )
                error.image2 = "The image url needs to end in .jpg, .png, or jpeg"
        }
        if (image5) {
            if (
                !(image5.endsWith(".jpg") || image5.endsWith(".png") || image5.endsWith(".jpeg"))
            )
                error.image5 = "The image url needs to end in .jpg, .png, or jpeg"
        }
        setError(error);
        // if (Object.keys(error).length > 0) {
        //     console.log(error)
        // }
        if (error.length > 0) {
            return setError({})

        }
        const spot = {
            country: country,
            address: address,
            city: city,
            state: state,
            description: description,
            price: price,
            name: name,
            lng: lng || 687.22,
            lat: lng || 345.22
        }

        const spotImagesArr = [];

        if (previewImage) {
            const prevImage = {
                preview: true,
                url: previewImage
            }
            spotImagesArr.push(prevImage)
        }

        if (image2) {
            const newImage2 = {
                preview: false,
                url: image2
            }
            spotImagesArr.push(newImage2)
        }
        if (image3) {
            const newImage3 = {
                preview: false,
                url: image3
            }
            spotImagesArr.push(newImage3)
        }
        if (image4) {
            const newImage4 = {
                preview: false,
                url: image4
            }
            spotImagesArr.push(newImage4)
        }
        if (image5) {
            const newImage5 = {
                preview: false,
                url: image5
            }
            spotImagesArr.push(newImage5)
        }


        const spotCreated = await dispatch(createOneSpotThunk(spot, spotImagesArr))

        if (spotCreated && spotCreated.id) {
            history.push(`/spots/${spotCreated.id}`)
        }

    }
    return (
        <div className="page-wrapper-create-spot">
            <form className="create-spot-form" onSubmit={onSubmit}>
                <div className="createanewspot"> Create a new Spot</div>
                {/* <br></br> */}
                <div className="whereisyourplacelocated">Where's your place located?</div>
                <br></br>
                <div className="guestwillonly">Guests will only get your exact address once they booked a reservation</div>
                <br></br>
                <label className="create-spot-label">Country</label>
                {error?.country && <span className="error">{error?.country}</span>}
                <input
                    type="text"
                    placeHolder="Country"
                    value={country}

                    onChange={e => setCountry(e?.target?.value)}
                />
                <label className="create-spot-label">Street Address</label>
                {error?.address && <span className="error">{error?.address}</span>}
                <input
                    type="text"
                    value={address}
                    placeHolder="Address"
                    onChange={e => setAddress(e?.target?.value)}
                />
                <label className="create-spot-label">City</label>

                {error?.city && <span className="error">{error?.city}</span>}
                <input
                    type="text"
                    value={city}
                    placeHolder="City"
                    onChange={e => setCity(e?.target?.value)}
                />
                <label className="create-spot-label">State</label>
                {error?.state && <span className="error">{error?.state}</span>}
                <input
                    type="text"
                    value={state}
                    placeHolder="State"
                    onChange={e => setState(e.target.value)}
                />

                <label className="create-spot-label">Description</label>
                <div className="description-caption">Mention the best features of your space, any special amentities<br></br> like
                      fast wif or parking, and what you love about the neighborhood.</div>
                {error?.description && <span className="error">{error?.description}</span>}
                <textarea className="description-text-box"
                    type="text"
                    value={description}
                    placeholder="Please write at least 30 characters"
                    onChange={e => setDescription(e.target.value)}
